                <h2 className='main-emoji'  >{emoji.emoji}</h2>
                <button className='emoji-btn' >ðŸŒ©</button>
               </div>
    })
    return(
        <div className='main-ctn'>
            <input  type='text' placeholder='insert your emoji here' name='emoji' onChange={props.change} value={props.value} id={props.id}/>
            <button  onClick={props.toggle}>Submit Emoji</button>
            {getEmoji}
        </div>
    )
}
