import React from 'react'
import Die from './Die'
import { nanoid } from 'nanoid'

export default function Main(){
    const [dice ,setDice] = React.useState(Dice())
    
    function GenerateNewDice(){
        setDice({value:Math.ceil(Math.random()*6) , isHeld :false , id:nanoid()})
    }
    
    function Dice(){
        const newDice = []
        for(let i = 0 ; i < 10 ; i++){
          newDice.push(GenerateNewDice())
          }
        return newDice
       }
       function Roll(){        
        setDice(oldDice => oldDice.map(help => {return help.isHeld ? help : GenerateNewDice()}))
       }
     function holdDice(id){
     setDice(prevDice => prevDice.map( held => {
             return held.id === id ? {...held ,isHeld : !held.isHeld } : held})
            )
     }
    const elementDice = dice.map(pervDice => <Die toggle={() =>holdDice(pervDice.id)}  isHeld={pervDice.isHeld} key={pervDice.id} value={pervDice.value} />)
    return(
        <main>
          <div className='main-ctn'>
            {elementDice}
          </div> 
          <button onClick={()=>Roll(dice.id)}>Roll</button>
        </main>
    )
}
