        for(let i = 0 ; i < 10 ; i++){
          newDice.push(Math.ceil(Math.random()*6))
          
        }
        console.log(newDice)
        return newDice
       }
    const elementDice = dice.map(pervDice => <Die key={pervDice[i]} value={pervDice} />)
    return(
        <main>
          <div className='main-ctn'>
            {elementDice}
          </div> 
          <button>roll</button>
        </main>
    )
}
