import React from 'react'

export default function Die(props){
    // const styles = {
    //      backgroundColor :props.isHeld ? '#59E391' : ''}
    const className =  props.isHeld ?  'die held' : 'die'
    return(
          <h2 className={props.rolling ? className + 'rolling-animation' : className} onClick={props.toggle}>{props.value}</h2>
    )
}
