import React , {useState}from 'react'
import Die from './Die'
import { nanoid } from 'nanoid'
import Confetti from 'react-confetti'

export default function Main(){
    const [dice ,setDice] = useState(Dice())
    const [tenzies , setTenzies] = useState(false)
    const [rollDice , setRollDice] = useState(false)

    React.useEffect(()=>{
      const allTrue = dice.every(die => die.isHeld)
      const firstValue = dice[0].value
      const allValue = dice.every(die => die.value === firstValue)
      if(allTrue && allValue){
        setTenzies(true)       
      } else {
        
      }
    },[dice])
    
    function GenerateNewDice(){
        return {value:Math.ceil(Math.random()*6) , isHeld :false , id:nanoid()}
    }
    
    function Dice(){
        const newDice = []
        for(let i = 0 ; i < 10 ; i++){
          newDice.push(GenerateNewDice())
          }
        return newDice
       }
       function Roll(){        
        if(!tenzies){
          setRollDice(true)
          setTimeout(()=>{
            setRollDice(false)
            setDice(oldDice => oldDice.map(help => {return help.isHeld ? help : GenerateNewDice()}))
          },1200)
          
        }else{
          setTenzies(false)
          setDice(Dice())
        }
       }
     function holdDice(id){
     setDice(prevDice => prevDice.map( held => {
             return held.id === id ? {...held ,isHeld : !held.isHeld } : held})
            )
     }
    const elementDice = dice.map(pervDice => <Die toggle={() =>holdDice(pervDice.id)}  isHeld={pervDice.isHeld} key={pervDice.id}
                                                  class={rollDice ? 'rolling-animation die' : 'die'} rolling={rollDice} value={pervDice.value} />)
    return(
        <main>
            <h1 className="title">Tenzies</h1>
            <p className="instructions">Roll until all dice are the same. Click each die to freeze it at its current value between rolls.</p>
          <div className={'main-ctn'}>
            {elementDice}
          </div> 
          <button onClick={Roll}>{tenzies ? 'New Game' : 'Roll'}</button>
          {tenzies && <Confetti width={1900}/>}
        </main>
    )
}
