import React from 'react'
import Die from './Die'
import { nanoid } from 'nanoid'

export default function Main(){
    const [dice ,setDice] = React.useState(Dice())
    function Dice(){
        const newDice = []
        for(let i = 0 ; i < 10 ; i++){
          newDice.push({value:Math.ceil(Math.random()*6) , isHeld :true , id:nanoid()})
          }
        return newDice
       }
       function Roll(){
        setDice(Dice())
       }
       function Held(){
         setDice(x => !x.isHeld)
      }
     
    const elementDice = dice.map(pervDice => <Die onClick={Held} isHeld={pervDice.isHeld} key={pervDice.id} value={pervDice.value} />)
    return(
        <main>
          <div className='main-ctn'>
            {elementDice}
          </div> 
          <button onClick={Roll}>Roll</button>
        </main>
    )
}
