import React from 'react'

export default function Die(props){
    const styles = {
         backgroundColor :props.isHeld ? '#59E391' : ''}
    // const className =  props.rolling ? 'die' : 'die rolling-animation'
    const displayValue = props.rolling ? <div className='rolling-animation'></div> : props.value
    const classNames = props.isHeld ? "die held" : "die";
    return(
      
          <div className={props.rolling ? classNames + " rolling-animation" : classNames} onClick={props.toggle}>
            {displayValue}
          </div>
      
    )
}
